mapper.select:
select

  distinct


from t_cms_good




  order by ${orderByClause}


  limit #{limitStart} , #{limitEnd}

mapper.insert:
insert into t_cms_good (good_id, good_level, parent_id, 
  good_name, has_count, good_dec, 
  good_brand, good_guige, good_end_time, 
  good_detail, good_image, crt_date
  )
values (#{goodId,jdbcType=INTEGER}, #{goodLevel,jdbcType=VARCHAR}, #{parentId,jdbcType=INTEGER}, 
  #{goodName,jdbcType=VARCHAR}, #{hasCount,jdbcType=INTEGER}, #{goodDec,jdbcType=VARCHAR}, 
  #{goodBrand,jdbcType=VARCHAR}, #{goodGuige,jdbcType=VARCHAR}, #{goodEndTime,jdbcType=TIMESTAMP}, 
  #{goodDetail,jdbcType=VARCHAR}, #{goodImage,jdbcType=VARCHAR}, #{crtDate,jdbcType=TIMESTAMP}
  )
